window.shuffle=function(d){var c=d.length,b,a;while(0!==c){a=Math.floor(Math.random()*c);c-=1;b=d[c];d[c]=d[a];d[a]=b}return d};window.suffix=function(b){var a=b%100;if(a>3&&a<21){return"th"}switch(a%10){case 1:return"st";case 2:return"nd";case 3:return"rd";default:return"th"}};window.checkSkip=function(a){if(a.last){$("#skip").unbind("click",skipHandler);$("#skip").remove()}else{$("#skip").unbind("click",skipHandler);$("#skip").bind("click",skipHandler)}};window.disableOptions=function(){$(".option_buttons").attr("disabled","disabled")};window.enableOptions=function(){$(".option_buttons").attr("disabled",false)};window.fillPoints=function(a){$("#points").text(a)};window.fillQuestionNumber=function(a){$("#question_th").text(a);$("#question_th_suffix").text(suffix(a))};window.fillUpsAndDown=function(a){$("#right").text(a.correct_score);$("#wrong").text(a.incorrect_score)};window.fillQuestion=function(a){var d=window.shuffle(a.options.slice(0));var c;$("#question_title").html(a.title);if(a.image_path){$("#question_image").attr("href",a.image_path);$("#question_image img").attr("src",a.image_path);$("#question_image").show()}else{$("#question_image").hide()}for(var b=0;b<d.length;b++){c=$("#option_"+b);c.text(d[b].title);c.attr("data-opt-id",d[b].id)}};